package main

import (
	"fmt"
	"strconv"
)

const bigNum = ""

func main() {
	large := 0

	for i := 0; i < len(bigNum)-12; i++ {
		if product(i) > large {
			large = product(i)
		}
	}
	fmt.Println(large)
}

func product(index int) int {
	a, _ := strconv.Atoi(bigNum[index : index+1])
	b, _ := strconv.Atoi(bigNum[index+1 : index+2])
	c, _ := strconv.Atoi(bigNum[index+2 : index+3])
	d, _ := strconv.Atoi(bigNum[index+3 : index+4])
	e, _ := strconv.Atoi(bigNum[index+4 : index+5])
	f, _ := strconv.Atoi(bigNum[index+5 : index+6])
	g, _ := strconv.Atoi(bigNum[index+6 : index+7])
	h, _ := strconv.Atoi(bigNum[index+7 : index+8])
	i, _ := strconv.Atoi(bigNum[index+8 : index+9])
	j, _ := strconv.Atoi(bigNum[index+9 : index+10])
	k, _ := strconv.Atoi(bigNum[index+10 : index+11])
	l, _ := strconv.Atoi(bigNum[index+11 : index+12])
	m, _ := strconv.Atoi(bigNum[index+12 : index+13])
	return a * b * c * d * e * f * g * h * i * j * k * l * m
}
